import { useState } from "react";

// tendra 2  parametros
const initialQuote = {
  text: 'Quote',
  author: 'Author'
}

function App() {
  const [quote, setQuote] = useState(initialQuote) // debemos crear esta initialQuote la creamo como un arreglo

  return (
    <div className="app">
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/7/77/Breaking_Bad_logo.svg"
        alt="logo"
      />
      <button>Get Another</button>
      {/* p lo llevamos aun componente diferente */}
      <p>{quote.text}<br /> {/* abrimos una sentencia js  y le decimos que nos imprima lo que esta en el estado quote.text */}

        <span> - {quote.author}
        </span>
      </p>
    </div>
  );
}

export default App;
