// ahora lo haremos con un spinner para ello crearemos un componente
// llamado spinner
// crearemos tambien un ahoja de estilos independiente para el spinner
import { useState, useEffect } from "react";
import { Quote } from "./componetes/Quote";
import Spinner from './componetes/Spinner.css' // ahora lo renderizamos

const initialQuote = {
  text: 'Quote',
  author: 'Author'
}

function App() {
  const [quote, setQuote] = useState(initialQuote)
  const [loading, setLoading] = useState(false)

  const updateQuote = async () => {

    setLoading(true)

    const url = 'https://www.breakingbadapi.com/api/quote/random'
    const res = await fetch(url)
    const [newQuote] = await res.json()
    const { quote: text, author } = newQuote

    setQuote({
      text,
      author
    })

    setLoading(false)
  }

  useEffect(() => {
    updateQuote()
  }, [])

  return (
    <div className="app">
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/7/77/Breaking_Bad_logo.svg"
        alt="logo"
      />
      <button onClick={() => updateQuote()}>Get Another</button>
      {
        // loading ? <h1>Loading...</h1> : <Quote quote={quote} />
        loading ? < Spinner /> : <Quote quote={quote} />

      }
    </div>
  );
}

export default App;
