// ahora realiceremos la peticion a la api para que nuestro state quote se carge la primera vez desde la api
// haremos la llmada a la api para la primera vez que se renderice el componente
//  y el hook que nos permmite hacer esta tarea es el useEffect

import { useState, useEffect } from "react";
import { Quote } from "./componetes/Quote";


// nuestro estado quote tendra 2  parametros
const initialQuote = {
  text: 'Quote',
  author: 'Author'
}

function App() {
  const [quote, setQuote] = useState(initialQuote) // debemos crear esta initialQuote la creamo como un arreglo

  // para usar useEffect lo llamaos aca , le pasamos una funcion o callback que se ejecutara cuando algun parametro de nuestro arreglo de dependencias cambie
  useEffect(() => {

  }, []) //en este caso no tendremos ninguna dependencia que estar escuchando , dejamos este arreglo vacio con el objetivo qu ela funcion se ejecute la primera ves
  // que se renderiza el componente y no se vuelva a ejecutar  


  return (
    <div className="app">
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/7/77/Breaking_Bad_logo.svg"
        alt="logo"
      />
      <button>Get Another</button>

      {/* le decimos al componente : recibe un quote que tiene el valor de nuestro estado quote  para enviarlo a quote.js*/}
      <Quote quote={quote} />
    </div>
  );
}

export default App;
