import { useState } from "react";
import { Quote } from "./componetes/Quote";


// nuestro estado quote tendra 2  parametros
const initialQuote = {
  text: 'Quote',
  author: 'Author'
}

function App() {
  const [quote, setQuote] = useState(initialQuote) // debemos crear esta initialQuote la creamo como un arreglo

  return (
    <div className="app">
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/7/77/Breaking_Bad_logo.svg"
        alt="logo"
      />
      <button>Get Another</button>

      {/* le decimos al componente : recibe un quote que tiene el valor de nuestro estado quote  para enviarlo a quote.js*/}
      <Quote quote={quote} />
    </div>
  );
}

export default App;
