// ahora haremos la actualizacion desde el boto haciendo click, vamos la boton y agreguemps el atrributo onclick
// alli ejecutaremos una funcion e 
// implementaremos unloading o un spiner para ello implementaremos un nuevo estado
import { useState, useEffect } from "react";
import { Quote } from "./componetes/Quote";

const initialQuote = {
  text: 'Quote',
  author: 'Author'
}

function App() {
  const [quote, setQuote] = useState(initialQuote) // debemos crear esta initialQuote la creamo como un arreglo
  // const [loading, setLoading] = useState(true) // ahora haremos un operador ternario , vamos donde imprimimos la quote
  const [loading, setLoading] = useState(false)
  // como cambiamos el valor inicial a false se muestra nuestro quote pero tenemos que hacer es que  nuestra funcion de actualizacion
  // cuando comience a actualizar el loading se coloque en true y cuando termine se coloque en false por lo tanto vamos a nuestra funcion de actualizacion


  const updateQuote = async () => {
    // diremos que cuando comience a actualizar el loadin se coloca en true
    setLoading(true)

    const url = 'https://www.breakingbadapi.com/api/quote/random'
    const res = await fetch(url)
    const [newQuote] = await res.json()
    const { quote: text, author } = newQuote

    setQuote({
      text,
      author
    })

    // diremos que cuando termine de actualizar el loadin se coloca en true
    setLoading(false)
  }

  useEffect(() => {
    updateQuote()
  }, [])

  return (
    <div className="app">
      <img
        src="https://upload.wikimedia.org/wikipedia/commons/7/77/Breaking_Bad_logo.svg"
        alt="logo"
      />
      {/* <button onClick={alert('click')}>Get Another</button> */}
      {/* <button onClick={() => alert('click')}>Get Another</button> */}
      {/* ahora llamaremos la funcion que creamos en vez del alert */}
      <button onClick={() => updateQuote()}>Get Another</button>

      {/* podemos ver que el alert se ejecuta inmediatamente , y eso es por que los parentesis
      amarillos esta indicando que se debe ejecutar directamente por lo tanto para evitar ese comportamiento la llamamos con funcion flecha*/}

      {/* operador ternario  y decimos si el loadng esta en true  esta cargando asi que muestrame esl texto loading
      y si esta en false quiere decir que ya no esta cargando asi que muestra el componente quote por ello mas abajo lo deje 
      comentado  ya que lo deje aca
      ahora solo se muestra loading asi que cambiaremos el valor de trure a false*/}
      {
        loading ? <h1>Loading...</h1> : <Quote quote={quote} />
      }
      {/* <Quote quote={quote} /> */}
    </div>
  );
}

export default App;
